('#panel-body').empty();            $('<div class="alert alert-info"><p>Filtrando dados...</p></div>').appendTo('#panel-body');            var funcionario_id = $('#funcionario_id').val();            var data_inicio = $('#data_inicio').val();            var data_fim = $('#data_fim').val();            $.ajax({                url: '<?= Router::url(array('Financeiros', 'filtrar')) ?>',                data: {                    funcionario_id: funcionario_id,                    data_inicio: data_inicio,                    data_fim: data_fim,                },                dataType: 'html',                type: 'post',                success: function(data, textStatus, jqXHR) {                    $('#panel-body').html(data);                }            });        });    });</script> 