r">                        <?php if( !empty($percentComission) ):?>                            R$ <?= Utils::real(Utils::comissao($percentComission, $entrada['valor_total'] - $entrada['desconto'])); ?>                        <?php else:?>                            ****                        <?php endif;?>                    </td>                </tr>            <?php                $iten++;                endforeach;            ?>        </tbody>    </table>    <!-- entrada do financiero -->    <h4 class="header-cast">Despesas.</h4> <hr style="margin-top:0px; margin-bottom:0px;">    <table class="table table-invoice font-size-table"  style="margin-top:0px;">        <thead>            <tr>                <th>#</th>                <th>Despesa </th>                <th class="text-center">Data Vencimento</th>                <th class="text-center">Tipo</th>                <th class="text-center">Valor (R$)</th>            </tr>        </thead>        <tbody><?php$iten = 1;$valorDespesas = 0;foreach ($despesas as $despesa):    $valorDespesas += $despesa['valor'];    ?>                <tr>                    <td><?= $iten ?></td>                    <td>    <?= ucwords($despesa['nome']) ?>.                    </td>                    <td class="text-center">                        <?                         if( $despesa['data_vencimento'] != '0000-00-00 00:00:00' ):                         echo Utils::convertData($despesa['data_vencimento']);                        else:                        echo '****';                        endif;                        ?>                    </td>                    <td class="text-center">    <?php if ($despesa['fixa'] == 0): ?>                            Despesas Gerais    <?php else: ?>                            Mensal, Fornecedores e credores    <?php endif; ?>                    </td>                    <td class="text-center">R$ <?= Utils