<table class="table table-condensed " id="dynamic-table">    <thead>        <tr>            <th style="width:15%">Cliente</th>            <th style="width:10%">Telefone</th>            <th style="width:10%">Ambiente</th>            <th style="width:11%">Data Reserva</th>            <th style="width:5%">Enviado</th>            <th style="width:5%">Confirmado</th>            <th style="width:5%"></th>        </tr>    </thead>    <tbody>    <?php    if (!empty($registros)):        foreach ($registros as $registro):    ?>            <tr>                <td ><?= $registro['cliente'] ?></td>                <td ><strong><?= $registro['telefone'] ?></strong></td>                <td ><?= $registro['ambiente'] ?></td>                <td ><?= Utils::convertData($registro['start']) ?></td>                <td>                    <?php if($registro['status']):?>                        <a class="label label-success"><i class="fa fa-check"></i></a>                    <?php else:?>                        <a class="label label-danger"><i class="fa fa-times"></i></a>                    <?php endif;?>                </td>                <td>                    <?php if($registro['confirm']):?>                        <a class="label label-success"><i class="fa fa-check"></i></a>                    <?php else:?>                        <a class="label label-danger confirm-envio-email" data-token="<?= $registro['token'];?>"><i class="fa fa-times"></i></a>                    <?php endif;?>                </td>                <td >                    <div class="btn-group btn-group-xs">                        <button class="btn btn-primary dropdown-toggle btn-xs" type="button" data-toggle="dropdown">                            Ações <span class="caret"></span>                        </button>                        <ul class="dropdown-menu" role="menu">                            <!--li><a class="" href="<?= Router::url(array('Reservas', 'listaConvidados', $registro['token'] )) ?>"><i class="fa fa-users"></i> Convidados</a></li-->                            <li><a class="" target="_blank" href="<?= $urlPDF .DS.md5($registro['id']) ?>"><i class="fa fa-print"></i> Gerar PDF</a></li>                            <li class="divider"></li>                            <li><a class="envioEmail" style="cursor:pointer" data-url="<?= Router::url(array('Reservas', 'envioEmail', $registro['token'])) ?>"><i class="fa fa-envelope"></i> Enviar E-mail</a></li>                        </ul>                    </div>                </td>            </tr>    <?php        endforeach;    endif;    ?>    </tbody></table><script>    $(document).ready(function() {        $('#dynamic-table').dataTable({            "language": {                "url": web_root + "View/webroot/js/data-tables/json/DataTable-Portuguese-Brasil.json"            }        });              $('.confirm-envio-email').click(function(){          var token = $(this).data('token');           $this     = $(this);                              if( token.length > 0 ){            $('#loading').fadeIn(500);            $.ajax({                url: web_root + 'Reservas/confirmReserva/',                data:{ token: token},                dataType: 'json',                type: 'post',                success: function (data) {                    $($this).addClass('label-success');                    $($this).removeClass('label-danger');                    $($this).find('i').removeClass('fa-times');                    $($this).find('i').addClass('fa-check');                                        bootsAlert(data);                                    }            });        }                 });                  });</script>