dalFormulario" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true">    <div class="modal-dialog">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal"><span aria-hidden="true">&times;</span><span class="sr-only">Close</span></button>                <h4 class="modal-title" id="myModalLabel">Reserva</h4>            </div>            <div class="modal-body append-body" id="append-body"> </div>          </div>    </div></div><script>    $(document).ready(function() {        $('.enviaForm').click(function() {            $('.clonado').find(' input[type=text], select ').each(function(key, value) {                if ($(value).val() == null || $(value).val() == "") {                    $(value).css('border-color', 'red');                } else {                    $(value).css('border-color', '#CBD5DD');                }            });        });    });    </script>