<div class="panel panel-default">	<div class="panel-body">		<div class="form-group">			<div class="col-md-3">				<small>Ambientes: </small>				<select name="ambientes_id" id="ambientes_id" class="form-control">                                            <?php 						foreach( $ambientes as $registro):						                                            ?>						<option value="<?= $registro['Ambiente']['id']?>" > <?= ucwords($registro['Ambiente']['nome'])?> </option>					<?php endforeach;?>				</select>			</div>			<div class="col-md-3">				<small>Data Inicio: </small>				<div class='input-group date datetimepicker2'>					<input type="text" class="form-control date_time" name="data_inicio" id="data_inicio" placeholder="DD/MM/AAAA">					<span class="input-group-addon"><span class="glyphicon glyphicon-calendar"></span>					</span>				</div>			</div>			<!--div class="col-md-3">				<small>Data Fim: </small>				<div class='input-group date datetimepicker2'>					<input type="text" class="form-control date_time" name="data_fim" id="data_fim" placeholder="DD/MM/AAAA">					<span class="input-group-addon"><span class="glyphicon glyphicon-calendar"></span>					</span>				</div>			</div-->		</div>		<div class="form-group">			<div class="col-md-2">				<button class="btn btn-primary btn-block" id="filtrar"> Filtrar</button>			</div>		</div>	</div></div><div class="panel panel-primary">    <div class="panel-heading">Lista de Reservas.</div>    <div class="panel-body" id="panel-body">    </div></div><script>$(document).ready(function() {     $('#filtrar').click(function(){        $('#panel-body').empty();                   var ambientes_id   = $('#ambientes_id').val();        var data_inicio    = $('#data_inicio').val();        var data_fim       = $('#data_fim').val();        var fraseLoading   = '';         if( data_inicio.length > 0 ){            fraseLoading = ' para a data <strong>'+ data_inicio +'</strong> ';        }                $('<div class="alert alert-info"><p>Filtrando Reservas '+fraseLoading+' ...</p></div>').appendTo('#panel-body');        $.ajax({            url: web_root + 'Reservas/filtrar',            data:{                ambientes_id: ambientes_id,                data_inicio:  data_inicio,                data_fim:     data_fim,            },            dataType: 'html',            type: 'post',            success: function (data, textStatus, jqXHR) {                $('#panel-body').html(data);                 }        });    });});$(document).on('click', '.envioEmail', function(){        $('#loading').fadeIn(500);        var url = $(this).data('url');        $.ajax({            url: url,            data:{},            dataType: 'json',            type: 'get',            success: function (data, textStatus, jqXHR) {                //if(data.erro == false){                    bootsAlert( data );                //}  else {                //}                $('#loading').fadeOut(500);            }        });});</script>  